function memoize(fn) {
    const cache = {};

    return function(...args) {
        const key = JSON.stringify(args);

        if (cache[key]) {
            return cache[key];  // Cache!
        }

        const result = fn(...args);
        cache[key] = result;
        return result;
    };
}

